# coding: utf-8with open('dev/test.format', 'r') as fin:    with open('dev/test.clean.format', 'w') as fout:        last_labels = ['O'] * 10        for row in fin:            if row == '\n':                last_labels = ['O'] * 10                fout.write('\n')            else:                data = row.split('\t')                token = data[0]                golden_label = data[1]                rank_labels = data[2:]                out = []                out.append(token)                out.append(golden_label)                for idx, rank_label in enumerate(rank_labels):                    rank_label = rank_label.strip()                    last_label = last_labels[idx]                    if rank_label.startswith('B-') or rank_label == 'O':                        last_labels[idx] = rank_label                        out.append(rank_label)                    else:                        # Start with I-                        if (last_label.startswith('B-') or last_label.startswith('I-')) and last_label[2:] == rank_label[2:]:                            last_labels[idx] = rank_label                            out.append(rank_label)                        else:                            # Invalid                            last_labels[idx] = 'O'                            out.append('O')                fout.write('\t'.join(out))                fout.write('\n')